    /**
     * Creates a menu item for a toggle action.
     */
    private JMenuItem createToggleMenuItem(Action action, boolean popup, boolean radioButton) {
        JMenuItem menuItem;
        if (radioButton) {
            menuItem = new JRadioButtonMenuItem();
        } else {
            menuItem = new JCheckBoxMenuItem();
