        if (!OperatingSystem.isMacOSX()) {
            fileMenu.addSeparator();
            addActionToMenu(ActionType.EXIT, fileMenu);
        }
        removeUselessSeparatorsAndEmptyMenus(menuBar);
        return menuBar;
