                    continue greeting;
                case 220:
                    break greeting;
                case 421:
                    throw new FTPException("FTP server not avaliable (421)");
                default:
                    throw new FTPException("Unexpected response from FTP server: " + respString);
            }
        }
        sendLine("USER " + username);
        resp = recvResponse();
        switch(resp) {
            case 230:
                break;
            case 331:
