            return new String[0];
        ArrayList<String> a = new ArrayList<String>();
        Enumeration<String> e = request.getSession().getAttributeNames();
        while (e.hasMoreElements()) {
            a.add(e.nextElement());
        }
        return a.toArray(new String[a.size()]);
