     * @see #getCookie
     * @see #setCookie
     */
    public String[] getCookieNames() {
        Cookie[] cookies = request.getCookies();
        if (cookies == null)
            return new String[0];
        String[] a = new String[cookies.length];
        int i = 0;
        for (Cookie c : cookies) {
            a[i++] = c.getName();
