            Map<String, String[]> pm = request.getParameterMap();
            for (String key : pm.keySet()) {
                if (key.startsWith(prefix) && key.endsWith("]")) {
                    m.put(key.substring(n, key.length() - 1), getParam(key));
                }
            }
        }
