        Search search = new Search();
        return search.UpcSearch(upc, mode, page);
    }

    /**
     * Search for a Listmania
     *
     * ~todo - Not sure if this works, never confirmed it, if it works let me know.~
     *
     * @since 1.0
     * @param listId
