			b = -1.0 / 15.0;
			assertDecoded(b, 43, 13);

			b = 0.0 / 0.333333;
			assertDecoded(b, 13, 43);

			b = -1.0 / 0.333333;
			assertDecoded(b, 43, 43);

			b = 1.0 / -0.75;
