	    		));
	    assertArrayEquals(
	    		result, Base64Coder.byte(8).getBytes(
	    			s, null
	    		));

	    assertArrayEquals(
	    		result, Base64Coder.byte(9).getBytes(
	    			s, (byte) 11
	    		));
	    assertArrayEquals(
	    		result, Base64Coder.byte(10).getBytes(
	    			s, (byte) 10
	    		));
	    assertArrayEquals(
	    		result, Base64Coder.byte(11).getBytes(
	    			s, (byte) 11
	    		));

	    assertArrayEquals(
	    		result, Base64Coder.char('\n').getBytes(
	    			s
	            ));	
	    assertArrayEquals(
	    		result, Base64Coder.char('\n').getBytes(
