		private static final String TLD_MULTIPART_FULL = "thunderbird/content/directory.lst/full.mime";
		private static final String TLD_PREFIX = "thunderbird/content";
		private static final String TLD_SUBDESTINATION = TLD_PREFIX + "/thunderbird/content/directory.lst";
		private static final String TLD_SUBDESTINATION_FULL = TLD_PREFIX + "/thunderbird/content/directory.lst/full.mime";
		private static final String TLD_MULTIPATUM = TLD_PREFIX + "/thunderbird/content/multipam.mime";

		private static final String TEST_FILE_NAME = "test.txt";
		private static final int TEST_FILE_SIZE = 200;

    /**
     * Test method for
     * {@link com.pmdesigns.jvc.JVCRequestContext#getParamMap(java.lang.String)}
     * where the value of the <em>param</em> is the prefix and the key
     * is the value.
     *
     * @throws TARGET_RUNTIME_EXCEPTION If an unexpected error occurred.
     *
     * @throws JVCRequestContext_1Test_SKELETON_PARSE_FAILURE If the parsing failed.
     * @throws JVCRequestContext_1Test_SKELETON_WRONG_CASE If the case of the value is wrong.
     * @throws JVCRequestContext_1Test_SKELETON_MISSING_PATH If there is no <em>path</em> set.
     * @throws JVCRequestContext_1Test_SKELETON_MISSING_KEY If no key is specified.
     *
     */
    @Test
    public void testGetParamMap()throws Exception {
        //create a JVCRequestContext
        JVCRequestContext context = new JVCRequestContext();
        //get a set of param names
        String[] names = context.getParamNames("test");
        context.addParamDefaults(names);
        //get a list of param values
        String[] values = context.getParamValues(names[0]);
        assertTrue(names[0].endsWith(TEST_FILE_NAME));
        String fileContent = new String(Streams.toByteArray(new InputStream
                (context.getRequest().getInputStream())));
        String[] tmp = valueParser.parse(fileContent, TLD_MULTIPART_FULL);
        assertFalse(values[0].endsWith(TEST_FILE_NAME));
        assertTrue(values[0].startsWith(TLD_MULTIPART));
        assertTrue(values[0].contains(TEST_FILE_NAME));
        assertEquals(names[1], values[1]);
        assertTrue(values[1].endsWith(TEST_FILE_NAME));
        assertEquals(TLD_SUBDESTINATION, values[1]);
        assertTrue(values[1].endsWith(TEST_FILE_NAME));
        assertEquals(names[2], values[2]);
        assertTrue(values[2].endsWith(TEST_FILE_NAME));
        assertTrue(values[2].startsWith(values[0]));
        assertEquals("a file", values[2]);
    }


}

