class JVCRequestContext_2Test {
		public final HttpServletRequest request;
		public final HttpServletResponse response;

		/**
		 * Construct the test.
		 * @param req the raw request
		 * @param resp the raw response
		 */
		JVCRequestContext_2Test(HttpServletRequest req, HttpServletResponse resp) {
			request = req;
			response = resp;
		}

		/**
		 * Test that the correct number of params are retrieved.
		 */
		@Test
		public void getParamNumber() throws Exception {
			Set<String> paramNames = getParamNames(request);
			assertEquals("wrong number of params", 7, paramNames.size());
			// There should be only three params.
			assertTrue("second param", paramNames.contains("second"));
			assertIn("third param", paramNames);
			paramNames.remove("first");
			assertTrue("other param", paramNames.isEmpty());
		}

		/**
		 * Test retrieving a non-existing parameter value.
		 */
		@Test
		public void getParamFails() throws Exception {
			Set<String> paramNames = getParamNames(request);
			request.setAttribute("first", "");
			boolean exists = Boolean.TRUE;
			try {
				response.getParameter(request.getParameter("first"));
			} catch (IllegalStateException ise) {
				exists = Boolean.FALSE;
			}
			assertTrue("non-existent param in request", exists);
			// The param was not found in the request, so the param was passed to
			// the servlet.
			assertTrue("param '1' not found", paramNames.contains("1"));
			// The url is now a POST even though the request contains no values
			assertNull("url is still a POST", request.getOriginalRequestURI());
			// No flash.
			assertEquals("wrong flash key", "param1", getParam("first"));
		}
	}

