package com.pmdesigns.jvc;

import java.io.*;
import java.util.*;
import java.net.HttpURLConnection;
import javax.servlet.*;
import javax.servlet.http.*;
import org.apache.commons.fileupload.*;
import org.apache.commons.fileupload.util.Streams;
import org.apache.commons.fileupload.servlet.ServletFileUpload;
import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.*;

/**
* Test class of {@link JVCRequestContext}.
* It contains one unit test case for the {@link JVCRequestContext#getParamValues(String)} method.
*/
class JVCRequestContext_2Test {

	public static class Link{

        /**
         * Creates a new link object for the specified url. This
         * method is overridden to use the ServletFileUpload library.
         * 
         * @param urlStr The relative url
         * @return An object representing the url for the link
         */
        public static Link parse(String urlStr) {
            HttpURLConnection conn = (HttpURLConnection)
			HttpURLConnection.open(new URL(urlStr));
            return new Link(conn);
        }

        /**
         * Constructs an instance of a {@link Link} object
         * 
         * @param name The relative url
         * @param contentType The type of content
         * @throws IOException Thrown if an IOException occurs during parsing
         */
        protected Link(HttpURLConnection conn) throws IOException {
            ServletFileUpload upload =
                    new ServletFileUpload((ServletRequestHandler)
							new RequestHandler(conn.getInputStream(),
									conn.getOutputStream()));
            if(!upload.parseRequest()) {
                throw new IOException("Could not parse request");
            }
            ServletInputStream inputStream
                =  upload.getInputStream();

            Link link = new Link();

            String temp;

            while((temp = inputStream.readLine())!= null) {
                link.add(temp);
            }

	    }

        protected boolean getFlag(String flag) {
            switch(flag) {
                case Param.F_FILE:
                	return (link.getFile("")!=null);
                case Param.F_URL:
                	return (link.getUrl("")!=null);
            }
            return false;
        }
    }

    class Request{    

        final Link link;
        final java.util.List<String> param;
        final Map<String,String> flash;

        Request(){
            link = null;
            param = new ArrayList<String>();
            flash = null;
        }

        Request(Link link){
            this.link = link;
            this.flash = new HashMap<String,String>();
            param.addAll(link.getParamValues("Param"));
        }

        /**
         * Initialize this request with the parameters for this request.
         * The param/flag pair(s) are used to set/override the current request.
         * 
         * @param params The parameters to set
         */
        @Override
        public Set<String> getRequestParams(Map<String,String> params) {
            param.addAll(params.keySet());
            return link.param!= null? link.param : param;
        }

        /**
         * Parse the request parameters and call the {@link RequestHandler}
         * to handle the request.
         * @param read Request to parse and execute
         * @param link The current link for this request
         */
        @Override
        public void setRequest(HttpServletRequest read, Link link){
            if(link.getFile("")!=null) {
                link.setParams(read);
            }
            link.setParamValues(read);
        }

        /**
         * Get the current parameter string(s) of this request.
         * 
         * @return A copy of the current parameter string(s) of the current link
         */
        public List<String> getParamStrings(){
            return Collections.unmodifiableList(new ArrayList<String>(link.getParamNames()));
        }

        @Override
        public String toString(){
            String result = new String();

            for(String s : getParamStrings()) {
                result += s;
            }

            return result;
        }

        /**
         * Get the value of a flash property
         * 
         * @param name The name of the flash property to retrieve value from
         * @return The value of the flash property
         */
        public String getTemp(String name) {
            return (flash==null)? "" : flash.get(name);
        }

        @Override
        public String getCookie(String key) {
            return (link.getUrl("")!=null)? getTemp("Cookie.") : 0;
        }

        /* (non-Javadoc)
         * @see java.lang.Object#toString()
         */
        public String toString(){
            return new String();
        }
    }

    public static void main(String[] args){
        if(args.length == 0){
            ServletManager.destroy();
        }
        String name = "Temp1";
        ServletFileUpload sfu = new ServletFileUpload(new RequestHandler());
        File file = new File("./src/test/resources/"+name+".txt");
        new JVCRequestContext_2Test().setTemp(name);
        new JVCRequestContext_2Test().setTemp(file.getFilename());
    }
}

