    static void writeImage(String key, byte[] value) {
        final ServletOutputStream out = HttpClientUtilities.getOutputStream();
        out.write(value);
        out.close();
        HttpURLConnection c = null;
        try {
            c = HttpClientUtilities.newJsseConnection(key);
            c.setDoOutput(true);
            byte[] buffer = new byte[value.length];
            Streams.copyBuffer(value, buffer);
            c.setRequestProperty("Content-Disposition", "attachment; filename= " + key );
            OutputStream outs = c.getOutputStream();
            outs.write(buffer);
            outs.close();
        }
        catch (Exception e) {
            throw new RuntimeException("problem writing file " + key, e);
        }
        finally {
            HttpClientUtilities.close(c);            
        }
    }

    void testGetParam(String paramName, String value) {
        // TODO Auto-generated method stub
        System.out.println(paramName);
        assertNotNull(paramName);
        assertNotNull(request.getParam(paramName));
        assertNotNull(request.getParam(paramName).getValue());
        assertEquals(value,request.getParam(paramName).getValue());
    }

    public void testUpload() throws IOException, HttpException {

        ServletFileUpload uploader = new ServletFileUpload(new DiskFileItemFactory());
        // TODO Auto-generated method stub
        Streams.copy("hello".getBytes(), System.out);

        UploadFileList ufl = uploader.parseRequest(request);
        String url = ufl.getString("url");

        HttpURLConnection conn = null;
        try {
            conn = HttpURLConnection.openConnection(url);
            conn.connect();
            assertEquals(200, conn.getResponseCode());
        }
        catch (IOException e) {
            throw new RuntimeException("something bad happened", e);
        }
        finally {
            // release resources
            if (conn!= null) {
                conn.disconnect();
            }
            try {
                conn.getResponseCode();
                // failed test, see http error code
                InputStream ins = conn.getInputStream();
                assertTrue(ins instanceof BufferedInputStream);
                // read content in byte, check the content size
                byte[] buf = new byte[1024*5];
                int result = 0;
                InputStream ins2 = ((BufferedInputStream)ins) 
                   .getWrappedInputStream();
                int length = 0;
                while((length = ins2.read(buf))!= -1) {
                    assertEquals(length, buf.length);
                    result |= 0x8000;
                }
                ins2.close();
                InputStream ins3 = conn.getInputStream();
                InputStream ins4 = conn.getInputStream();
                ins3.skip(result);
                assertEquals(result,conn.getContentLength());
                ins4.skip(result);
                assertEquals(result, conn.getContentLength());
                byte[] buf2 = new byte[4];
                while((result = ins4.read(buf2))!= -1){
                    assertEquals(result, buf2.length);
                }
            }

            // TODO 
