/**
* Test class of {@link JVCRequestContext}.
* It contains one unit test case for the {@link JVCRequestContext#getAttribute(String)} method.
*/
class JVCRequestContext_3Test {

	/**
	 * Make a test case where a user is redirected to a URL that is not valid.
	 */
	@Test
	void shouldRedirectWithoutAttr() throws IOException, ServletException {

		HttpURLConnection request =
			(HttpURLConnection)
				new URL("https://www.google.com").openConnection();
			request.disconnect();
			String response =
			ServletFileUpload.read(request.getInputStream());

	// Make and store temp key/value map in a local var
			HttpServletResponse response = new MockHttpServletResponse("This is a test");
			Map<String,String> flash = new HashMap<String,String>();

			// Fill up flash with temporary key/value strings
			ServletFileUpload upload = new ServletFileUpload();

			JVCFileUpload fileupload = (JVCFileUpload)upload.parseRequest(request.getInputStream(),response);

			Map<String,String> flashTemp = new HashMap<String,String>();
			fileupload.extractedFields().forEach(t -> flashTemp.put(t.key, t.value)); 

			// Perform test
			assertEquals("This is the test", flashTemp.get("This is the test")); 

	// Set the flash attribute (and restore the state)
	    request.setAttribute(FileUploadHandler.KEY_ATTR, flash);
			request.setAttribute(FileUploadHandler.FLASH_ATTR, flashTemp);

		request.shutdown();					
		response.flushBuffer();
		assertEquals("This is a test",response.getBuffer().toString());

	// Ensure the flash is correct, and that the flash contains no temp keys
	// Set the flash attribute (and save the state)
	request.setAttribute(FileUploadHandler.KEY_ATTR, flash);
	request.setAttribute(FileUploadHandler.FLASH_ATTR, flash);


	}

