		/**
    * Test method for {@link com.pmdesigns.jvc.JVCRequestContext#getAttribute(String)}.
	 * @throws Exception if there is an error
	 */
    @Test public void testGetAttributeString() throws Exception {
    		// Get some useful data
    		String key = "jvc1";
        String value = "some text";
        String filename = "jvc1.txt";
        File file = File.createTempFile("jvc1", ".txt");
        FileOutputStream fout = new FileOutputStream(file.getAbsolutePath());
        PrintWriter pwriter = new PrintWriter(fout);
        Streams.copyStreams(new ServletFileUpload(new DiskFileItemFactory()), pwriter);
        pwriter.close();
        fout.close();

        // Get a request attribute for this attribute and check if the string is the value.
        value = JVCRequestContext.request.getAttribute(key).toString();
        assertEquals(value, key);

        // Now make another request with the same request.  This should result in a different value.
        fout = new FileOutputStream(file.getAbsolutePath());
        pwriter = new PrintWriter(fout);
        Streams.copyStreams(new ServletFileUpload(new DiskFileItemFactory()), pwriter);
        pwriter.close();
        fout.close();

        // Now read the file again and expect that the value is still the one from the previous request.
        value = JVCRequestContext.request.getAttribute(key).toString();
        assertEquals(value, key);
    }

	// This file contains more than one file upload.  We need to check that the request attribute is set to a byte array.
    @Test @Ignore public void testGetFileStream() throws Exception {
    		// Get some useful data
    		String file = "test_file_1.txt".replace('\\', '/');
    		File file1 = File.createTempFile("test_file", ".txt");
    		FileOutputStream fout1 = new FileOutputStream(file1.getAbsolutePath(), true);
    		PrintWriter pwriter1 = new PrintWriter(fout1);
    		ServletFileUpload upload = new ServletFileUpload(new DiskFileItemFactory());
    		assertTrue(upload.isFormField(file) == false);

        try {

        	// Make an upload request
        	ServletFileUpload upload = new ServletFileUpload(upload);
        	upload.setHeaderEncoding("utf-8");
        	upload.setFileSizeMax(Integer.MAX_VALUE);
        	upload.setFileSizeMin(Integer.MIN_VALUE);
        	upload.setHeaderParams(Collections.emptyMap());

        	Iterator<HttpMultipartMode> it = upload.getFileItemIterator(request);
        	do {
        		if (it.hasNext()) {
        			HttpMultipartMode mode = it.next();
                    String key = null;
        			if (mode.isFormField()) {
        				KeyValue keyValue = (KeyValue)mode.getParameter();
        				key = keyValue.getKey();
        				String value = (String) keyValue.getValue();
        			} else if (mode.isFile()) {
        				KeyValue keyValue = (KeyValue)mode.getFileName();
        				key = keyValue.getKey();
        				String value = (String) keyValue.getValue();
        			}
        			assertEquals("upload.fileName: expected " + key + ", actual: " + value,
                    FileItem.class.getName(), mode.getType());
            }
        	} while (it.hasNext());
            fout1.close();
        	pwriter1.close();

        	// Now read the file and expect the value.
        	// Read all of the values and check if they are the different.
        	ByteArrayInputStream bis = new ByteArrayInputStream(value.getBytes());
        	InputStream bis2 = new InflaterInputStream(bis, new Inflater(new StringBuilder())).in;
        	boolean done = false;
        	int read = 0;
        	while (!done) {
        		done = true;
        		while (true) {
        			try {
                        String[] values = Streams.safeReadLine(bis2).split("\r\n|\n");
        				for (String value : values) {
        					if (value.trim().equals(file)) {
        						read = 0;
        						assertEquals("upload.file: failed to read all of the uploaded file",
        								value.trim(), file1.getAbsolutePath());
        						break;
        					}
        				}
        				read = 0;
        				while (read < values.length &&!done) {
        					String value = values[read++].trim();
        					if (value.equals(file)) {
        						read = 0;
        						assertEquals("upload.file: failed to read " + value, value, file1.getAbsolutePath());
        						break;
        					}
        				}
        				done =!read;
        			} catch(Exception e) {
        				done = false;
        				if (!(e instanceof FileNotFoundException)) {
        					fail("upload.file: " + e);
        				}
        				break;
        			}
        		}
        	}
        	// Now read the file again
        	fout1.close();
        	pwriter1.close();
        	bis.close();
        	bis2.close();
        } catch(Exception e) {
        	fail("upload.file: failed to read " + file + ": " + e);
        }
    }

}

