	@Test
    public void testGetAttributeFileUpload() {

        // make this test happy
        // there are four potential problems with the file upload field
        // a) if the original request comes back with a 200 (success)
        // b) if there is no name in the uploaded file
        // c) if there are multiple files uploaded (for multi-selection)
        // d) if the original request comes back with a 400 (confirmation)

        // a)
        File origFile = new File("request.txt");
        assertNull(jvcRequestContext.getAttribute("name"));
        UploadedFile upload = ServletFileUpload.getInstance("text/plain").parseKeyValue("name=originalfile");
        assertNotNull(upload);
        File uploadFile = upload.getFile();
        assertEquals("originalfile", uploadFile.getName());
        assertNull(jvcRequestContext.getAttribute("name"));

        // b)
        // if the original request comes back with a error response and the server
        // sends a 200 (success)
        long contentLength = origFile.length();
        HttpServletRequest servletRequest = new MockHttpServletRequest(origFile, origFile.length());
        HttpServletResponse servletResponse = new MockHttpServletResponse();
        String requestStr = servletRequest.getMethod() + " " + servletRequest.getPathInfo();
        HttpURLConnection urlConnection = (HttpURLConnection) servletRequest.getConnection();
        urlConnection.setFixedLengthStreamingMode(contentLength);
        assertTrue(urlConnection.getResponseCode() == 200);
        assertTrue(servletResponse.contains(requestStr));

        // c)
        byte[] origContents = origFile.getContent();
        int origLength = origContents.length;
        assertNull(jvcRequestContext.getAttribute("name"));

        // d)
        File uploadedFile = null;
        try {
            origContents = new byte[0];
            upload =  new UploadedFile(origContents, contentLength);
            uploadFile = upload.getFile();
            assertNull(jvcRequestContext.getAttribute("name"));
            assertTrue(uploadFile.length() == origLength);
            uploadedFile = uploadFile;
        } catch (Exception e) {
            // we expect the file to not get created but the name in the uploadedfile
            // was not set.
            assertTrue(e.getMessage().indexOf("name not found in uploadedfile") > -1);
            return;
        }
        assertNotNull(uploadedFile);
        assertEquals(contentLength, uploadedFile.length());
        assertNotNull(jvcRequestContext.getAttribute("name"));
