		String test = "/";
		Object session = null;
		Object test1 = null;

		assertTrue( "getSessionAttr - null session attribute",
				request.getSession() == null );

		session = request.getSession();
		assertTrue( "getSessionAttr - session attribute is initialized",
				session!= null);

		test1 = request.getSession().getAttribute("test");
		assertTrue( "getSessionAttr - expected attribute value is "+test1+" in "+session, 
				test1 == test);

		request.getSession().removeAttribute("test");
		session = request.getSession();
		assertTrue( "getSessionAttr - session attribute should be null in "+session,
				session == null);		
	}


public void testGetSessionAttrNames() {

		HttpServletRequest request = new JVCRequestContext( request );
		String test = "/";
		String[] attrNames = new String[]{ "test" };

		assertTrue( "getSessionAttrNames - null attribute name list",
				Arrays.equals( request.getSession().getAttributeNames(), attrNames ) );

		Set<String> names = request.getSession().getAttributeNames();

		for( String name : names)
			assertTrue( "getSessionAttrNames - missing "+name, name.equals( test) );

		request.getSession().setAttribute("key", "value");
		String name = "key";
		session = request.getSession();
        assertTrue( "getSessionAttrNames - set attribute to value", name.equals( "value"));
		int i = 1;
		while( request.getSession().getAttribute(name) == null ) {
			if( ++i >=3){
				throw new Exception("session attribute was not set");
			}
		}
		assertTrue( "getSessionAttrNames - set attribute again", name.equals( "value"));

		names = request.getSession().getAttributeNames();
		assertTrue( "getSessionAttrNames - should be no longer empty", names.isEmpty());		
	}


