		Map flashMap = new HashMap();

		HttpServletRequest request = new JVCRequestContext( request );
		Object session = null;

		flashMap.put("flash", flashMap);
		session = request.getSession();
		assertTrue( "getFlash - expected Flash value in session map with "+session,
				session!= null &&!session.toString().equals("flash"));

		request.getSession().setAttribute("flash", "something");
		session = request.getSession();
		assertTrue( "getFlash - expected Flash value after session attribute set",
				session!= null &&!session.toString().equals("flash"));

