package com.pmdesigns.jvc;

import java.io.*;
import java.util.*;
import java.net.HttpURLConnection;
import javax.servlet.*;
import javax.servlet.http.*;
import org.apache.commons.fileupload.*;
import org.apache.commons.fileupload.util.Streams;
import org.apache.commons.fileupload.servlet.ServletFileUpload;
import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.*;

/**
* Test class of {@link JVCRequestContext}.
* It contains one unit test case for the {@link JVCRequestContext#getCookie(String)} method.
*/
class JVCRequestContext_5Test {
		/**
		 * A generic test for the getCookie() method.
		 */
    @Test
    public void testGetCookie() {

        // Create a new request object to use to initialize the request
        HttpServletRequest request = new JVCRequest(new HttpServletRequestWrapper(httpRequest));
        HttpServletResponse response = new HttpServletResponseWrapper(httpResponse);

		// Now use the request object to get a Cookie.
		Cookie[] cookies = request.getCookies();
		assertEquals("An unescaped cookie value was found", cookies.length, 0);

		// If the value for the cookie is null, we don't want to add it, so return null
		request.addHeader("Cookie", "");
		Cookie[] cookiesAgain = request.getCookies();
		assertEquals("An unescaped cookie had been returned unexpectedly", 0, cookiesAgain.length);

		// Add a second cookie to replace the last one
        response.setHeader("Set-Cookie", "foo=bar; Version=1");
		// Now get a new request object to have the other cookie added that we haven't added yet.
		request = new HttpServletRequestWrapper(httpRequest);
		cookies = request.getCookies();
		assertEquals("An unescaped cookie value was found", cookies.length, 1);
		response.setHeader("Set-Cookie", "");
		cookiesAgain = request.getCookies();
		assertEquals("An escaping cookie value was found.", 0, cookiesAgain.length);


        // Make sure that the request is now returning an unescaped cookie
        // That is, we expect null to be returned.
		request.setHeader("Cookie", "foo=bar; Version=1");
		cookies = request.getCookies();
		assertEquals("An escaped cookie value was returned", cookies.length, 1);
		assertEquals(cookies[0].getName(), "jsoup.foo");
        response.setHeader("Set-Cookie", "");
		cookiesAgain = request.getCookies();
		assertEquals("No change", 0, cookiesAgain.length);

        // Now see if the same cookie was returned twice, this time with no cookie value.
		request.setHeader("Cookie", "");
		cookies = request.getCookies();
        assertEquals("No change", 0, cookies.length);
        cookiesAgain = request.getCookies();
        assertEquals("No change", 0, cookiesAgain.length);

        // Add two cookies to ensure that the same cookie value is not returned twice
        request.addHeader("Set-Cookie", "foo=bar; Version=1");
        request.addHeader("Cookie", "foo=bar; Version=1");
		cookies = request.getCookies();
		response.setHeader("Set-Cookie", "");
        cookiesAgain = request.getCookies();
        assertEquals("Same cookie value was not returned two times", 3, cookiesAgain.length);

    	// The following two cases were found and work when this code was being tested.
        // For a request with no value for the cookie, which was added after the cookie was set.
        // We should return a null value.

    	request.clearCookies();
        response.clearCookies();
        cookies = request.getCookies();
        assertEquals("Null cookie value is returned", 0, cookies.length);
        cookiesAgain = request.getCookies();
        assertEquals("Null cookie value is returned", 0, cookiesAgain.length);

        // Add a non-escaped cookie and make sure it is not returned.
        request.addHeader("Set-Cookie", "foo=bar, Version=1");
        cookies = request.getCookies();
        assertEquals("A non escaped cookie value was returned", 0, cookies.length);
        request.removeHeader("Set-Cookie");

        // Add a non-escaped cookie and make sure it is returned.
        request.addHeader("Set-Cookie", "foo=bar, Version=1; Path=/");
        cookies = request.getCookies();
        assertEquals("An escaped cookie value was returned", 1, cookies.length);
        response.setHeader("Set-Cookie", "");

        // Add a non-escaped cookie and make sure it is returned with the same value for the name.
        request.addHeader("Set-Cookie", "foo=bar, Version=1");
        cookies = request.getCookies();
        assertEquals("An unescaped cookie value was returned again", 1, cookies.length);
        response.setHeader("Set-Cookie", "");

    	// The following two cases were found and work when this code was being tested.
        // For a request with no value for the cookie, which was added after the cookie was set.
        // We should return a null value.

    	request.clearCookies();
        response.clearCookies();
        cookies = request.getCookies();
        assertEquals("Null cookie value is returned", 0, cookies.length);
        cookiesAgain = request.getCookies();
        assertEquals("Null cookie value is returned", 0, cookiesAgain.length);

        // Add a non-escaped cookie and make sure it is not returned.
        request.addHeader("Set-Cookie", "foo=bar, Version=1");
        cookies = request.getCookies();
        assertEquals("An escaped cookie value was removed", 0, cookies.length);
        response.setHeader("Set-Cookie", "");

        // Add a non-escaped cookie and make sure it is not returned.
        request.addHeader("Set-Cookie", "foo=bar, Version=1; Path=/");
        cookies = request.getCookies();
        assertEquals("An escaped cookie value was added", 0, cookies.length);
        response.setHeader("Set-Cookie", "");

        // Add a non-escaped cookie and make sure it is returned.
}
}

