        val = "/index.html";
        request.setRequestURI(val);
        cookie = request.getCookie(key);
        assertTrue(cookie!= null);
        assertEquals(val, cookie.getValue());

        request.getRequestURL().toString();
        cookie = request.getCookie(key);
        assertTrue(cookie!= null);
        assertEquals(val, cookie.getValue());
    }

