	 * @throws Exception is thrown in case of errors.
	 */
    @Test
    public void getCookieTest2() throws Exception{
        assertNotNull(request);
        assertEquals(request, getCookie(JVCRequestContext.RequestType.GET.name()));
        assertNull(getCookie(null));

        String key = "key-1";
        String val = "value-1";
        Integer i = new Integer(1);
        String iStr = String.valueOf(i);
        Cookie cookie = getCookie(key);
        assertFalse(cookie == null);
        assertEquals(val, cookie.getValue());

        String value = request.getCookie(key);
        assertEquals(val, value);


        val = "/somepath/";
        request.setSessionAttribute(key, val);
        value = request.getCookie(key);
        assertEquals(val, value);






    	String newPath = "a";
        String oldPath = "a";
        String resPath = "a";
        String[] paths = {oldPath, val, newPath};

        for (int i=0; i< paths.length ; i++) {
            request.setAttribute(oldPath, "/");
            HttpSession ses = request.getSession();
            ses.setAttribute(newPath, paths[i] );
        }
        value = request.getCookie(key);
        assertEquals(newPath, value);



    	ses = request.getSession();
        ses.setAttribute(newPath, "/a/");
        value = request.getCookie(key);
        assertEquals(newPath, value);


    	ses = request.getSession();
        ses.setAttribute(newPath, "/a=z/");
        value = request.getCookie(key);
        assertEquals(newPath, value);

        ses.setAttribute("xsl", "/xsl");
        value = request.getCookie(key);
        assertEquals("/xsl", value);
    }



}

