import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.*;

/**
* Test class of {@link JVCRequestContext}.
* It contains one unit test case for the {@link JVCRequestContext#makeLink(String, String)} method.
*/
class JVCRequestContext_7Test {

	private final List<File> htmlFiles;

	public JVCRequestContext_7Test(File folder, int num) throws Exception {
		htmlFiles = Arrays.asList(folder.listFiles()).subList(0, num + 1);
		assertTrue("List " + folder.list().length + " not found", htmlFiles.size() > 0);
	}

	/**
	 * This test executes a batch of Upload operations.
	 */
	@Test
	public void makeLinkBatch() throws Exception {
		doRequest(1, 1, 1, 1, 1);
		assertTrue("Number of files not matching.", htmlFiles.size() >= 1);
		for (int i = 1; i < htmlFiles.size(); i++) {
			assertTrue("File not matching.", isEqual(htmlFiles.get(i), htmlFiles.get(i - 1)));
		}
	}

	private void doRequest(int num, int batchSize, int batch, int index, int numBatches) throws FileNotFoundException, IOException {
		UploadManager uploadManager = new UploadManager();
		uploadManager.startAsync();
		ArrayList<String> files = new ArrayList<String>();
		Hashtable<String, String> flash = new Hashtable<String, String>();
		HtmlServletFileUpload servletFileUpload = new HtmlServletFileUpload(batchSize);
		servletFileUpload.setDynamicContentType("text/plain; charset=us-ascii");
		for (int i = 0; i < num; i++) {
			File source = htmlFiles.get(index * batch + i);
			File target = new File(servletFileUpload.getSubmittedFileName(i));
			String uploadFileName = new String(Files.readAllBytes(source.toPath()));
			uploadFileName = uploadFileName.replaceAll("src=" + source.getCanonicalPath(), "")
										.replaceAll("\\\\", "/");
			uploadFileName = uploadFileName.replaceAll("\\?{count}", String.valueOf(batch));
			String targetName = new String(Files.readAllBytes(target.toPath()));
			targetName = targetName.replaceAll("\\\\", "/");
			targetName = targetName.replaceAll("\\?{count}", String.valueOf(numBatches));
			files.add(Files.readAllBytes(source.toPath()));
			files.add(Files.readAllBytes(target.toPath()));
			String key = uploadFileName + "," + targetName;
			flash.put(key, "loaded");
		}
		for (String uploadFileName : files) {
			uploadManager.add(uploadFileName, new FileInputStream(Files.readAllBytes(source.toPath())));
		}
		servletFileUpload.setContainerContext(this);
		servletFileUpload.setFileSizeMax(1);
		try {
			ArrayList<Request> requests = new ArrayList<Request>();
			for(int i=0; i>=0; i++) {
				Request request = servletFileUpload.process(batchSize == 1?  "src=" + (i!= 0?
                                String.valueOf(files.get(i).replaceAll("\\?{count}", String.valueOf(i + batch))): files.get(i)) :
                                String.valueOf(files.get(i)), null);
	            if (request!= null) {
	            	requests.add(request);
	            }
			}
			servletFileUpload = new HtmlServletFileUpload(1);
			servletFileUpload.setContainerContext(this);
			servletFileUpload.setFileSizeMax(1);
			servletFileUpload.upload((ServletContext) this, batch, num, requests);
		} finally {
			uploadManager.stopAsync();
		}
	}

	private boolean isEqual(File a, File b) {
		if (a.getAbsolutePath().equals(b.getAbsolutePath())) {
			return true;
		} else {
			return false;
		}
	}

	/**
	 * Verify that a set of key/value pairs are equal if they have
	 * all the values being compared.
	 * @param key the key of the first key/value pair, it will be made
	 *   camelCase and its end with one character to distinguish it from
	 *   the one with underscore.
	 * @param values the second set of values being compared
	 */
	private void assertTrue(String key, Object values) {
		assertEquals("Failed to find " + key + " in " + values, values, values.getClass().isArray()? values : Arrays.toString(values.toString()));
	}

}

