class JVCRequestContext_7Test {
		String request;
		char[] token = {'#','', '/', '?', '&','', ':', '=', '+', '$', '/'};
		String encoding = null;
    /**
     * Test the get parameter value is returned as appropriate.
     * There is no specific test for parameter value extraction because there is no need for such.
     * @throws Exception if an unexpected problem occurs.
     */
    @Test
    public void testParamsEncoded() throws Exception {
        // Create a request with an empty body and no headers to test
        Request  request = new Request();

        // Create a JVCRequestContext and then use it to set the request body
        HttpxRequestContext context = new HttpxRequestContext(request);
        context.setParamsEncoded(true);

        // Generate a key and value from a random string as generated by an external tool
        String key=GenerateKey();
        String value=GenerateValue();
        context.request.setParameter(key, value);

        // Test that it returns the expected parameter value and response headers
        String body = new String(context.request.getBody());
        assertEquals("Request Parameters Encoded", "key=value", body);
        assertEquals("Set parameter value with JVC Request Context", value, context.request.getParameter(key));
        assertEquals("Request Headers", "Accept, Content-Type, Content-Length, Cache-Control, Authorization, User-Agent, HTTP_1_1", context.request.getRequestHeader("User-Agent"));
    }

    /**
     * Test HttpRequest.setFile() is called properly.
     * There must be no extra data in the file upload.
     */
    @Test
    public void testFileSet() throws Exception {
        // Create a request to test
        Request  request = new Request();

        // Generate a key and value from a random string as generated by an external tool
        String key=GenerateKey();
        String value=GenerateValue();

        // Create a FileUpload object
        FileUpload fu = new ServletFileUpload();
        List<FileItem> items = fu.parseRequest(request);
        for (FileItem item : items) {
            // There must be an item key
            String itemKey = item.getFieldName();
            // There may be multiple key values
            String[] keys = item.getFieldKeys();
            String itemValue = getParameter(itemKey,true);
            item.setFileName(key);
            item.setFieldName(itemKey);
            item.setFieldValues(itemValue);
        }

        // Upload the file
        StringTokenizer fileList = new StringTokenizer(items.get(0).getFilename(), "");
        boolean first = fileList.hasMoreTokens();
        File file=fileList.nextToken();
        fu.setFileItem(file, file.getName());

        // Set the request body to use any existing file
        String body = new String(request.getBody());
        assertEquals("Empty Request Body", null, body);
        request.setRequestEntity(new StringRequestEntity(file, file.length(),
        new MultipartRequestEntity(new FileInputStream(file), 
        file.getName(),
        new String(uploadContent("Content")))));

        // Generate a test key and value
        String key = generateKey(32);
        String value = generateValue(32);

        // Reset to request parameters
        request.setRequestHeaders("key="+key+";Value="+value);
        request.setRequestParameter("File", ""+first);

        // Test that the file is successfully uploaded
        request.setRequestHeader("User-Agent", "JVE");

        // Test whether the parameters are not lost
        assertFalse("File is unexpectedly lost", request.getParameters().containsKey("key"));
        assertFalse("File should not be used for the remaining request", request.getParameters().containsKey(key));
        assertNull("File cannot be used for the previously uploaded request", request.getParameters().get(key));

        // Reset the test key and value
        first =!first;
        key = "<"+key+">";
        value = "<"+value+">";
        request.setRequestParameter(key, value);
    }

    private String uploadContent(String content) {
        return new String(Streams.toByteArray(new ByteArrayInputStream(content.getBytes("US-ASCII"))),
            "US-ASCII");
    }

    private String generateValue(int bits) {
        StringBuffer buf = new StringBuffer();
        int n=0;
        for(char octet: token) {
            if(octet==' ')
                n='#'+n;
            buf.append(Integer.toHexString(n) + octet);
            n++;
        }
        return buf.toString();
    }

    private char[] UploadEncoding(char[] content){
        return(ServletFileUpload.isMultipartContent(content))?
                content: ((ServletFileUpload)uploadContent(content).getStream()).getChunkedStream().getChunk();
    }

}

