        String link = c.makeLink(null, "/foo", null);
        assertEquals("<a href=\"/foo\">null</a>", link);
        String href = c.makeLink("", "/foo", null);
        assertEquals("<a href=\"/foo\">/foo</a>", href);
        String href2 = c.makeLink("/foo", "/foo/", null);
        assertEquals("<a href=\"/foo\">/foo/</a>", href2);
        List<HttpServletRequest> reqs = new ArrayList<HttpServletRequest>();
        HttpServletRequest req = null;
        try {
            req = new SimpleServletRequestWrapper(new DefaultHttpServletRequest("POST", null));
        } catch(Exception e) {
            fail(e.getClass().getSimpleName());
        }
        c.addRequest(req);
        req = null;
        try {
            req = new SimpleServletRequestWrapper(new DefaultHttpServletRequest("POST", "/foo"));
        } catch(Exception e) {
            fail(e.getClass().getSimpleName());
        }
        c.addRequest(req);

        String contextPath = c.makeLink("/jsp/jvc1.jsp", "/foo", null);
        String flash = c.getFlash();
        assertNotNull(flash);
        Map<String, String> tempMap = c.getTempMap();
        assertEquals(1, tempMap.size());
        assertEquals("jvc1.jsp", tempMap.get("context-path"));
        contextPath = c.makeLink("/jsp/jvc2.jsp", "../foo", null);
        flash = c.getFlash();
        assertNotNull(flash);

        assertNotNull(c.getRequestContext());       

        c.getRequestContext().setContextPath("/javacdc");
        String actual_contextPath = c.makeLink("/jscdc/jvc3.jsp", "/foo", null);
        assertEquals("<a href=\"/javacdc/jvc3.jsp\">/jscdc/jvc3.jsp</a>", actual_contextPath);

        //test a redirect response
        c.addRequest(new SimpleServletRequestWrapper(new DefaultHttpServletRequest("POST", "/foo")));

