        path = c.makeLink("/jvcc/jvc5.jsp", "/foo", null);
        expected_link_result("/jvcc/jvc5.jsp", "/foo", path, "jvc5.jsp");        
    }

    /**
     * test a full redirection based on the flash
     * 
     * 
     * @param flash The key that led to the flash
     * @param expected the path where the flash comes back
     * @param path the link that resulted from the flash
     * @param expected the result path when the flash is processed
