    void testGetFlash() throws Exception {
        HttpURLConnection request = new HttpURLConnection("post", "http://www.pmdesigns.com");
        // We're not using JVCRequests here--it's a bit too loose for testing...
        request.setResponseCode(302);
        HttpURLConnection response = new HttpURLConnection("post", "http://www.pmdesigns.com");
        response.setRequestProperty("Content-Type", "application/json");
        String payload = Streams.toString(request.getInputStream());
        String flash = response.getHeaderField("X-Web-Flash");
        String flash2 = request.getResponseFields().get("X-Web-Flash");
        assertEquals("Expected \"X-Web-Flash\" content for post request, but got: " + flash+ " instead",
                "X-Web-Flash", flash);
        assertEquals("Expected \"X-Web-Flash2\" content for post request, but got: " + flash2+ " instead",
                 "X-Web-Flash2", flash2);
        assertEquals("Expected \"flash\" as a key in the flash and not present in the request", "flash", flash);
        assertTrue("Expected \"flash\" value of \"flash\" to be present in the flash but got: "+flash+" instead", flash.contains("flash"));        
    }


    @Test
    void getFlash_defaults() throws Exception {
        // Create a test request. It has no content and will result in
        // a 403, not an unhandled 404 response because the servlet 
        // can't handle a redirect.  The servlet would just have to
        // ignore the client agent that the request has passed along

        HttpURLConnection request = new HttpURLConnection("post", "http://www.pmdesigns.com");

        Streams.toString(new InputStreamResponseFilter());            
        assertEquals("Unexpected error message in stream response: "+request.getErrorMessage(), 0, request.getErrorStream().available());
        assertTrue(request.getCalledUrl().endsWith("/redirect"));

    }


    /**
     * Create a HttpURLConnection request for the 'post' servlet.
     */
    private final static String servletRequestUrl = "post";
    private void testServletURLRequest(String form) throws IOException {
           String clientAgent = "JVC; Version=1.0.0";
            HttpURLConnection request = null;
            HttpFields responseFields = new HttpFields();
            request = new HttpURLConnection(servletRequestUrl, false);
            ServletFileUpload upload = new ServletFileUpload(new DiskFileFactory());
            List<FileItem> files = null;
            if (form.isEmpty()) {
                files = upload.parseRequest(request);
            } else {
                files = upload.parseRequest(request, form);
            }
            FileItem item;
            Iterator<FileItem> iter = files.iterator();
            while (iter.hasNext()) {
                item = iter.next();
                if (item.isFormField()) {
                    item.readFields(request);
                    // The servlet should only send basic auth
                    if (username.equals("User") && password.equals("PmDesigns")) { 
                        String headerEncoding = request.getFieldHeader("Content-Encoding");
                        assertTrue("Expected text/plain encoding header", 
                                    headerEncoding.contentEquals("TEXT PLAINTEXT"));
                    }
                } else { 
                    item.getFields(responseFields);
                }
            }

        assertEquals(4, responseFields.getFieldNames().size());

        Field authKeyField = responseFields.getField("X-Servlet-Key");
        assertTrue("X-Servlet-Auth should be 'x-request'", authKeyField.getFieldName().equals("X-Servlet-Key"));

        Field versionField = responseFields.getField("X-Servlet-Version");

        if (versionField.getFieldName().equals("Server")) {
            versionField = null;
        }

        Field requestURLField = responseFields.getField("X-Servlet-Request-URL");

        request = null;
        assertTrue("Expected x-request to contain'request' for form fields",requestFields.contains(requestURLField));

        assertEquals("Expected x-request to have the servlet's request URL",servletRequestUrl,requestURLField.getValue());

        if (requestFields.contains(authKeyField)) {
            Field authValueField = requestFields.getField(authKeyField.getName());
            if (!authValueField.getType().isArray()) {
                String authValue = authValueField.getValue();
                request = new HttpURLConnection("POST", authValue, authValueField.getName(), requestFields, responseFields.getContentType());
                assertEquals("Expected 'POST' request to return with HTTP code " + request.getResponseCode(),
                            401, request.getResponseCode());
            }
        } else {
