    /**
     * Verify that the {@link JVCRequestContext#getFlash(String)} is correctly
     * called when a flash key is used to identify the file upload.
     *
     * @throws IOException if there is an I/O error
     * @throws UnsupportedEncodingException if there is an I/O error
     */
    @Test
    public void testGetFlash_FlashKeyUsed() throws IOException, UnsupportedEncodingException {
        // Create an HttpServletRequest and a HttpServletResponse
        JVCHttpServletRequest request = new JVCHttpServletRequest();
        JVCHttpServletResponse response = new JVCHttpServletResponse();

        // set up the request object.
        request.setMethod("POST");
        request.setQueryString("testkey");
        request.setContentType("application/x-www-form-urlencoded");
        request.setContentLength(3);
        request.setContent(createExampleFileContent("key1=value1\nr=value2"));
        WebdavClient client = new JVCWebDavClient();
        client.init(new Properties());

        // create a response object to be used
        response.sendRedirect("/index.jsp");
        assertEquals("POST",request.getMethod());
        assertEquals("testkey=r=value2",request.getQueryString());

        // execute the request.
        HttpURLConnection conn = request.send(client);        

        // Check values - response should have values
        assertNotNull(conn.getContentLength());
        assertNotNull(conn.getHeaderField("ContentMD5"));

        // Check that the Content-MD5 is correct!
        assertEquals("D8B9EC9CC9F6AAD3",conn.getHeaderField("Content-MD5"));
    }

    /**
     * Verify that the {@link JVCRequestContext#getFlash(String)} is correctly
     * called when a flash key is used to locate the form data.
     *
     * @throws IOException if there is an I/O error
     * @throws UnsupportedEncodingException if there is an I/O error
     */
    @Test
    public void testGetFlash_NoFlashKeyUsed() throws IOException, UnsupportedEncodingException {
        // Create an HttpServletRequest and a HttpServletResponse
        JVCHttpServletRequest request = new JVCHttpServletRequest();
        JVCHttpServletResponse response = new JVCHttpServletResponse();

        // set up the request object.
        request.setMethod("POST");
        request.setQueryString("testkey");
        request.setContentType("application/x-www-form-urlencoded");
        request.setContentLength(3);
        request.setContent(createExampleFileContent("testkey=r,key_=value1,key2=value2,x,y"));
        WebdavClient client = new JVCWebDavClient();
        client.init(new Properties());

        // create a response object to be used
        response.sendRedirect("/index.jsp");

        // check that the response is correct.
        assertNull("Response should be null.",response);
    }


}

