        }
    }

    static void ctor(final Class<?> klass) {
        // construct a HashMap instance of {@value JVCGenerator#FIELD_CAPACITY} elements
        // using this Map directly for HashMap initialization
        final HashMap<String,String> map = new HashMap<>(JVCGenerator.FIELD_CAPACITY);

        // construct an instance of the Map using the default constructor of HashMap
        final Map<String,String> m = new HashMap<>();
        Constructor<Map> cons = klass.getConstructor(HashMap.class);
        cons.setAccessible(true);
        cons.newInstance(m);
        Field field = Map.class.getDeclaredField("size");
        field.setAccessible(true);
        field.set(m,JVCGenerator.FIELD_CAPACITY);
        field = Map.class.getDeclaredField("keySet");
        field.setAccessible(true);
        field.set(map,JVCGenerator.FIELD_CAPACITY);

        // set the Map instance of {@value JVCGenerator#FIELD_CAPACITY}
        field = map.getClass().getDeclaredField("size");
        field.setAccessible(true);
        field.set(map,JVCGenerator.FIELD_CAPACITY);
        field = map.getClass().getDeclaredField("keySet");
        field.setAccessible(true);
        field.set(map,JVCGenerator.FIELD_CAPACITY);

        // set map instance with the HashMap as the map
        try {
            map.forEach(x -> {
                map.put(x.toString(),x.toString());
            });
        } catch (Exception e) {
            System.err.println(e.getMessage() == null?""+e:e.getMessage());
        }

        // set the new HashMap instance
        field = map.getClass().getDeclaredField("size");
        field.setAccessible(true);
        field.set(map,JVCGenerator.FIELD_CAPACITY - 1);
        field = map.getClass().getDeclaredField("keySet");
        field.setAccessible(true);
        field.set(map,JVCGenerator.FIELD_CAPACITY);
    } 

    /**
	 * Generate a {@link JVCGenerator} to write the JVC server
	 */
	private static JVCGenerator gen = new JVCGenerator("JHC");


    /**
	 * The HTTP request with which this servlet will be invoked
	 *
	 * @return the HTTP request for which this servlet will be invoked
     */
    @Override
    @Test
    public void test() throws ServletException, IOException, InterruptedException {
        final JVCDocument xml = gen.writeJVC("http://pmserv/helloJVC");

        // write a server header to the specified request
        final HttpServletRequest request = gen.writeMock();

        // execute the JVC dispatcher method to invoke the JVCGenerator
        final String result = gen.dispatch(request);
        assertNull(result);
    }
}

