class JVCDispatcherTest {

    // constants to determine what we expect
    // javadoc: <jvc servlet return codes>
    private final int CANCELLED = -1;
    private final int METHOD_CONFLICT = -2;
    private final int METHOD_NOT_SUPPORTED = -3;

    @Test
    public void test() throws IOException {

        // set request context
        JVCRequestContext ctx;
        try {
            ctx = JVCRequestContext.get(getServletContext());
        } catch (javax.servlet.jsp.jstl.core.JspParseException e) {
            System.err.println("Javax.servlet.jsp.jstl.core.JspParseException at line 14: " + e.getMessage());
            e.printStackTrace();
            return;
        }
        System.err.println(ctx.get("SERVER_NAME"));    	

