        // run generation and catch exceptions
        try {
            gen.generate("src");
        } catch (Exception e) {
            // if an error is generated, log error in log file
            System.err.println(e.getMessage());
            System.exit(1);
            return;
        }

        // log output of JVCGenerator
        res.setHeader("Content-Type", "text/html");
        res.setHeader("Access-Control-Allow-Origin", "*");
        res.setHeader("Content-Length", "10");
