@Test
void testTextAtSingleRow() {
    CharTable ct = new CharTable(new Block[][]{{new Block('a'), new Block('b')}, {new Block('c'), new Block('d')}}); // create a char table with four blocks containing 'a', 'b', 'c', and 'd'
