            try {
                checkPreferencesFolder();
                out = new FileOutputStream(new File(getPreferencesFolder(), PREFERENCES_FILE));
                this.preferencesProperties.storeToXML(out, "Portable user preferences 3.0");
            } finally {
                if (out != null) {
                    out.close();
                    this.exist = true;
